import React, { useState } from 'react';
import axios from 'axios';
import './Home.css';

export default function Home() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    // Function to handle sending a message
    const sendMessage = async () => {
        if (!input.trim()) return;

        // Add user's message to the chat
        const newMessages = [...messages, { role: 'user', content: input }];
        setMessages(newMessages);
        setInput('');  // Clear the input field

        try {
            // Send request to backend API
            const response = await axios.post('http://localhost:3011/api/chat', {
                messages: newMessages
            });

            // Add AI's response to the chat
            setMessages([...newMessages, { role: 'assistant', content: response.data.content }]);
        } catch (error) {
            console.error('Error sending message:', error);
            setMessages([...newMessages, { role: 'assistant', content: 'Error: Could not retrieve response' }]);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-window">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.role}`}>
                        <p>{message.content}</p>
                    </div>
                ))}
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Type your message..."
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
}
