import React, { useState } from 'react';
import axios from 'axios';
import './Sql.css';

export default function Sql() {
    const [query, setQuery] = useState('');
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:3011/api/process-query', { query });
            setResult(response.data);
        } catch (error) {
            console.error('Error:', error);
            setResult({ error: 'Failed to process query. Please try again.' });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="sql-container">
            <h1>SQL Query Analyzer</h1>
            <form onSubmit={handleSubmit} className="sql-form">
                <textarea
                    placeholder="Enter your SQL query here..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="sql-textarea"
                ></textarea>
                <button type="submit" className="sql-submit" disabled={loading}>
                    {loading ? 'Analyzing...' : 'Submit Query'}
                </button>
            </form>
            {result && (
                <div className="sql-result">
                    <h2>Result:</h2>
                    {result.error ? (
                        <p className="error">{result.error}</p>
                    ) : (
                        <div>
                            <p><strong>Query:</strong> {result.query}</p>
                            <p><strong>Analysis:</strong> {result.analysis}</p>
                            <p><strong>Result:</strong> {result.result}</p>
                            {result.queryResult && result.queryResult.length > 0 && (
                                <div className="query-result">
                                    <h3>Query Output:</h3>
                                    <table className="result-table">
                                        <thead>
                                            <tr>
                                                {Object.keys(result.queryResult[0]).map((key) => (
                                                    <th key={key}>{key}</th>
                                                ))}
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {result.queryResult.map((row, index) => (
                                                <tr key={index}>
                                                    {Object.values(row).map((value, i) => (
                                                        <td key={i}>{value}</td>
                                                    ))}
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            )}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}
