import React, { useState } from 'react';
import axios from 'axios';
import './Sql.css';

export default function Sql() {
    const [query, setQuery] = useState(''); // To store the user's input
    const [result, setResult] = useState(null); // To store the API response
    const [loading, setLoading] = useState(false); // To track loading state

    // Function to handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on form submit
        setLoading(true); // Set loading state

        try {
            // Send the SQL query to the API
            const response = await axios.post('http://localhost:3011/api/process-query', { query });
            setResult(response.data); // Update the result state with API response
        } catch (error) {
            console.error('Error:', error);
            setResult({ error: 'Failed to process query. Please try again.' });
        } finally {
            setLoading(false); // Reset loading state
        }
    };

    return (
        <div className="sql-container">
            <h1>SQL Query Analyzer</h1>
            <form onSubmit={handleSubmit} className="sql-form">
                <textarea
                    placeholder="Enter your SQL query here..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="sql-textarea"
                ></textarea>
                <button type="submit" className="sql-submit" disabled={loading}>
                    {loading ? 'Analyzing...' : 'Submit Query'}
                </button>
            </form>
            {result && (
                <div className="sql-result">
                    <h2>Result:</h2>
                    {result.error ? (
                        <p className="error">{result.error}</p>
                    ) : (
                        <div>
                            <p><strong>Query:</strong> {result.query}</p>
                            <p><strong>Analysis:</strong> {result.analysis}</p>
                            <p><strong>Result:</strong> {result.result}</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}
